import React, { useState, useEffect } from 'react';
import axios from 'axios';

const maskAccount = (username) => {
  if (!username) return '';
  if (username.length <= 4) return username;
  return '****' + username.slice(-4);
};

const formatCurrency = (amount) => {
  return amount.toLocaleString('vi-VN', { style: 'currency', currency: 'VND' });
};

const Dashboard = () => {
  const [balance, setBalance] = useState(0);
  const [transactions, setTransactions] = useState([]);
  const [transferData, setTransferData] = useState({
    toUsername: '',
    amount: '',
  });
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) {
      window.location.href = '/login';
      return;
    }
    // Fetch CSRF token first
    axios.get('/api/csrf-token')
      .then(csrfRes => {
        const csrfToken = csrfRes.data.csrfToken;
        axios.get('/api/transactions/history', {
          headers: { Authorization: `Bearer ${token}`, 'X-CSRF-Token': csrfToken }
        })
          .then(res => {
            setBalance(res.data.balance);
            setTransactions(res.data.transactions);
          })
          .catch(err => {
            setError('Không tải được lịch sử giao dịch');
          });
      })
      .catch(() => setError('Không lấy được token bảo mật'));
  }, [token]);

  const handleChange = e => {
    setTransferData({ ...transferData, [e.target.name]: e.target.value });
  };

  const handleTransfer = async e => {
    e.preventDefault();
    setMessage(null);
    setError(null);
    setLoading(true);
    try {
      // Fetch CSRF token first
      const csrfRes = await axios.get('/api/csrf-token');
      const csrfToken = csrfRes.data.csrfToken;

      const res = await axios.post('/api/transactions/transfer', transferData, {
        headers: { Authorization: `Bearer ${token}`, 'X-CSRF-Token': csrfToken }
      });
      setBalance(historyRes.data.balance);
      setTransactions(historyRes.data.transactions);
    } catch (err) {
      setError(err.response?.data?.error || 'Transfer failed');
    }
  };

  return (
    <div className="container">
      <h2>Dashboard</h2>
      <p><strong>Balance:</strong> ${balance.toFixed(2)}</p>
      {message && <div className="alert alert-success">{message}</div>}
      {error && <div className="alert alert-error">{error}</div>}

      <h3>Transfer Money</h3>
      <form onSubmit={handleTransfer}>
        <div className="form-group">
          <label>To Username:</label>
          <input name="toUsername" value={transferData.toUsername} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Amount:</label>
          <input name="amount" type="number" step="0.01" value={transferData.amount} onChange={handleChange} required />
        </div>
        <button type="submit" className="btn btn-primary">Transfer</button>
      </form>

      <h3>Transaction History</h3>
      <table className="table">
        <thead>
          <tr>
            <th>From</th>
            <th>To</th>
            <th>Amount</th>
            <th>Type</th>
            <th>Status</th>
            <th>Description</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map(tx => (
            <tr key={tx.id}>
              <td>{maskAccount(tx.fromUsername)}</td>
              <td>{maskAccount(tx.toUsername)}</td>
              <td>${tx.amount.toFixed(2)}</td>
              <td>{tx.type}</td>
              <td>{tx.status}</td>
              <td>{tx.description}</td>
              <td>{new Date(tx.createdAt).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
