import React, { useCallback, useEffect, useMemo } from "react";
import { useNavigate } from "react-router-dom";
import NotificationContext from "../context/NotificationContext";

const statusBadgeClass = (status) => {
  if (status === "completed") return "bg-success-subtle text-success";
  if (status === "pending") return "bg-warning-subtle text-warning";
  return "bg-danger-subtle text-danger";
};

const formatCurrency = (amount) => {
  return amount.toLocaleString("vi-VN", {
    style: "currency",
    currency: "VND",
  });
};

const NotificationsPage = () => {
  const navigate = useNavigate();
  const {
    notifications,
    unreadCount,
    markAllAsRead,
    markAsRead,
    refresh,
    readMap,
  } = React.useContext(NotificationContext);

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      navigate("/login");
    }
  }, [token, navigate]);

  useEffect(() => {
    refresh();
  }, [refresh]);

  const grouped = useMemo(() => {
    return notifications.reduce((acc, item) => {
      const day = new Date(item.createdAt).toLocaleDateString("vi-VN");
      acc[day] = acc[day] || [];
      acc[day].push(item);
      return acc;
    }, {});
  }, [notifications]);

  const sortedDays = useMemo(() => {
    return Object.keys(grouped).sort((a, b) => {
      const da = new Date(a.split("/").reverse().join("-"));
      const db = new Date(b.split("/").reverse().join("-"));
      return db - da;
    });
  }, [grouped]);

  const handleMarkAll = useCallback(() => {
    markAllAsRead();
  }, [markAllAsRead]);

  const handleCardClick = useCallback(
    (item) => {
      markAsRead(item.id);
    },
    [markAsRead]
  );

  return (
    <div className="notifications-page">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <h2 className="mb-0">Thông báo</h2>
          <small className="text-muted">
            {unreadCount > 0
              ? `${unreadCount} thông báo chưa đọc`
              : "Bạn đã đọc tất cả thông báo"}
          </small>
        </div>
        {unreadCount > 0 && (
          <button className="btn btn-outline-primary" onClick={handleMarkAll}>
            Đánh dấu đã đọc
          </button>
        )}
      </div>
      {sortedDays.length === 0 ? (
        <div className="text-center text-muted py-5">
          Chưa có thông báo giao dịch.
        </div>
      ) : (
        sortedDays.map((day) => (
          <section key={day} className="mb-4">
            <p className="fw-semibold text-uppercase small text-muted mb-2">
              {day}
            </p>
            <div className="row g-3">
              {grouped[day].map((item) => {
                const isUnread = !readMap[item.id];
                const amount = formatCurrency(Math.abs(item.amount));
                const prefix = item.amount >= 0 ? "+" : "-";
                return (
                  <div className="col-md-6" key={item.id}>
                    <div
                      className={`card notification-card ${
                        isUnread ? "notification-card--unread" : ""
                      }`}
                      role="button"
                      onClick={() => handleCardClick(item)}
                    >
                      <div className="card-body">
                        <div className="d-flex justify-content-between align-items-start mb-3">
                          <div>
                            <h5 className="card-title mb-1">{item.title}</h5>
                            <p className="card-subtitle text-muted mb-0">
                              {item.description}
                            </p>
                          </div>
                          <div
                            className={`badge ${statusBadgeClass(item.status)}`}
                          >
                            {item.status === "completed"
                              ? "Hoàn thành"
                              : item.status === "pending"
                              ? "Đang xử lý"
                              : "Thất bại"}
                          </div>
                        </div>
                        <div className="d-flex justify-content-between align-items-center">
                          <span className="transaction-amount">
                            {prefix}
                            {amount}
                          </span>
                          <span className="transaction-meta">
                            <i
                              className="bi bi-clock-history me-1"
                              aria-hidden
                            ></i>
                            {new Date(item.createdAt).toLocaleTimeString(
                              "vi-VN",
                              {
                                hour: "2-digit",
                                minute: "2-digit",
                              }
                            )}
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </section>
        ))
      )}
    </div>
  );
};

export default NotificationsPage;
