import React, { useState } from 'react';
import axios from 'axios';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
  });
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setMessage(null);
    setError(null);
    setLoading(true);
    try {
      // Fetch CSRF token first
      const csrfRes = await axios.get('/api/csrf-token');
      const csrfToken = csrfRes.data.csrfToken;

      const res = await axios.post('/api/auth/register', formData, {
        headers: { 'X-CSRF-Token': csrfToken }
      });
      setMessage(res.data.message || 'Đăng ký thành công');
      setFormData({ username: '', email: '', password: '' });
    } catch (err) {
      setError(err.response?.data?.error || 'Đăng ký thất bại');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="register-container">
      <h2>Đăng ký tài khoản</h2>
      {message && <div className="alert alert-success">{message}</div>}
      {error && <div className="alert alert-error">{error}</div>}
      {loading && <div className="loading">Đang xử lý...</div>}
      <form onSubmit={handleSubmit} className="form">
        <div className="form-group">
          <label>Tên đăng nhập:</label>
          <input name="username" value={formData.username} onChange={handleChange} required placeholder="Nhập tên đăng nhập" />
        </div>
        <div className="form-group">
          <label>Email:</label>
          <input name="email" type="email" value={formData.email} onChange={handleChange} required placeholder="Nhập email" />
        </div>
        <div className="form-group">
          <label>Mật khẩu:</label>
          <input name="password" type="password" value={formData.password} onChange={handleChange} required placeholder="Nhập mật khẩu" />
        </div>
        <button type="submit" className="btn btn-primary" disabled={loading}>Đăng ký</button>
      </form>
    </div>
  );
};

export default Register;
