import React, { useState, useEffect } from "react";
import api from "../services/api";

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    let mounted = true;
    const fetchData = async () => {
      try {
        const res = await api.get("/api/transactions/history");
        if (!mounted) return;
        // server returns balance and transactions; fetch user info from token
        const raw = localStorage.getItem("token");
        const payload = raw ? JSON.parse(atob(raw.split(".")[1])) : null;
        setUser({
          email: payload?.email || "-",
          id: payload?.id,
          balance: res.data.balance,
        });
      } catch (err) {
        setError(
          err.response?.data?.error || "Không thể lấy thông tin người dùng"
        );
      } finally {
        setLoading(false);
      }
    };
    fetchData();
    return () => (mounted = false);
  }, []);

  if (loading) return <div className="card p-3">Đang tải...</div>;
  if (error) return <div className="alert alert-error">{error}</div>;

  return (
    <div className="card p-3">
      <h3>Hồ sơ người dùng</h3>
      <p>
        <strong>ID:</strong> {user.id}
      </p>
      <p>
        <strong>Email:</strong> {user.email}
      </p>
      <p>
        <strong>Số dư:</strong> {user.balance?.toLocaleString("vi-VN")} VND
      </p>
    </div>
  );
};

export default UserProfile;
