import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    otp: '',
  });
  const [step, setStep] = useState(1); // 1: login, 2: MFA
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);
  const [tempToken, setTempToken] = useState(null);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleLogin = async e => {
    e.preventDefault();
    setMessage(null);
    setError(null);
    try {
      const res = await axios.post('/api/auth/login', { email: formData.email, password: formData.password });
      if (res.data.requiresMFA) {
        setStep(2);
        setTempToken(res.data.tempToken);
        setMessage('OTP sent to your email. Please enter it.');
      } else {
        localStorage.setItem('token', res.data.token);
        localStorage.setItem('refreshToken', res.data.refreshToken);
        window.location.href = '/dashboard';
      }
    } catch (err) {
      setError(err.response?.data?.error || 'Login failed');
    }
  };

  const handleMFA = async e => {
    e.preventDefault();
    setMessage(null);
    setError(null);
    try {
      const res = await axios.post('/api/auth/login/mfa', { tempToken, otp: formData.otp });
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('refreshToken', res.data.refreshToken);
      window.location.href = '/dashboard';
    } catch (err) {
      setError(err.response?.data?.error || 'MFA failed');
    }
  };

  return (
    <div className="container">
      <h2>Login</h2>
      {message && <div className="alert alert-success">{message}</div>}
      {error && <div className="alert alert-error">{error}</div>}
      {step === 1 ? (
        <form onSubmit={handleLogin}>
          <div className="form-group">
            <label>Email:</label>
            <input name="email" type="email" value={formData.email} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label>Password:</label>
            <input name="password" type="password" value={formData.password} onChange={handleChange} required />
          </div>
          <button type="submit" className="btn btn-primary">Login</button>
        </form>
      ) : (
        <form onSubmit={handleMFA}>
          <div className="form-group">
            <label>OTP:</label>
            <input name="otp" value={formData.otp} onChange={handleChange} required />
          </div>
          <button type="submit" className="btn btn-primary">Verify OTP</button>
        </form>
      )}
    </div>
  );
};

export default Login;
