import React, { useState, useEffect } from "react";
import api from "../services/api";

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [msg, setMsg] = useState(null);
  const [pwCurrent, setPwCurrent] = useState("");
  const [pwNew, setPwNew] = useState("");
  const [mfaEnabled, setMfaEnabled] = useState(false);
  const [mfaQR, setMfaQR] = useState(null);
  const [mfaSecret, setMfaSecret] = useState("");
  const [mfaToken, setMfaToken] = useState("");

  useEffect(() => {
    let mounted = true;
    const fetchProfile = async () => {
      try {
        const res = await api.get("/api/auth/me");
        if (!mounted) return;
        const u = res.data.user;
        setUser(u);
        setUsername(u.username || "");
        setEmail(u.email || "");
        setMfaEnabled(!!u.mfaEnabled);
      } catch (err) {
        setError(
          err.response?.data?.error || "Không thể lấy thông tin người dùng"
        );
      } finally {
        setLoading(false);
      }
    };
    fetchProfile();
    return () => (mounted = false);
  }, []);

  const saveProfile = async () => {
    setMsg(null);
    try {
      const res = await api.put("/api/auth/me", { username, email });
      setMsg(res.data.message || "Đã cập nhật");
      setUser((s) => ({ ...s, username, email }));
    } catch (err) {
      setMsg(err.response?.data?.error || "Cập nhật thất bại");
    }
  };

  const changePassword = async () => {
    setMsg(null);
    try {
      const res = await api.post("/api/auth/change-password", {
        currentPassword: pwCurrent,
        newPassword: pwNew,
      });
      setMsg(res.data.message || "Mật khẩu đã thay đổi");
      setPwCurrent("");
      setPwNew("");
    } catch (err) {
      setMsg(err.response?.data?.error || "Thay đổi mật khẩu thất bại");
    }
  };

  const toggleMfa = async () => {
    setMsg(null);
    try {
      const res = await api.post("/api/auth/mfa/toggle", {});
      setMfaEnabled(!!res.data.mfaEnabled);
      setMsg(`MFA ${res.data.mfaEnabled ? "đã bật" : "đã tắt"}`);
    } catch (err) {
      setMsg(err.response?.data?.error || "Không thể thay đổi MFA");
    }
  };

  const setupMfa = async () => {
    setMsg(null);
    try {
      const res = await api.post("/api/auth/mfa/setup", {});
      setMfaQR(res.data.qr);
      setMfaSecret(res.data.secret);
      setMsg("Đã tạo QR/secret. Hãy quét và nhập mã để xác nhận.");
    } catch (err) {
      setMsg(err.response?.data?.error || "Không thể khởi tạo MFA");
    }
  };

  const verifyMfa = async () => {
    setMsg(null);
    try {
      const res = await api.post("/api/auth/mfa/verify", { token: mfaToken });
      setMsg(res.data.message || "Đã bật MFA");
      setMfaEnabled(true);
      setMfaQR(null);
      setMfaSecret("");
      setMfaToken("");
    } catch (err) {
      setMsg(err.response?.data?.error || "Mã xác nhận không đúng");
    }
  };

  if (loading) return <div className="card p-3">Đang tải...</div>;
  if (error) return <div className="alert alert-danger">{error}</div>;

  return (
    <div className="card p-3">
      <h3>Hồ sơ người dùng</h3>
      {msg && <div className="alert alert-info">{msg}</div>}

      <div className="mb-2">
        <label className="form-label">ID</label>
        <div>{user.id}</div>
      </div>

      <div className="mb-2">
        <label className="form-label">Tên người dùng</label>
        <input
          className="form-control"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
      </div>

      <div className="mb-2">
        <label className="form-label">Email</label>
        <input
          className="form-control"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>

      <div className="mb-3">
        <button className="btn btn-primary me-2" onClick={saveProfile}>
          Lưu thay đổi
        </button>
      </div>

      <hr />

      <h5>Đổi mật khẩu</h5>
      <div className="mb-2">
        <label className="form-label">Mật khẩu hiện tại</label>
        <input
          type="password"
          className="form-control"
          value={pwCurrent}
          onChange={(e) => setPwCurrent(e.target.value)}
        />
      </div>
      <div className="mb-2">
        <label className="form-label">Mật khẩu mới</label>
        <input
          type="password"
          className="form-control"
          value={pwNew}
          onChange={(e) => setPwNew(e.target.value)}
        />
      </div>
      <div>
        <button className="btn btn-warning" onClick={changePassword}>
          Thay đổi mật khẩu
        </button>
      </div>

      <hr />

      <h5>Bảo mật 2 lớp (MFA)</h5>
      <div className="mb-2">
        Trạng thái:{" "}
        {mfaEnabled ? (
          <span className="badge bg-success">Bật</span>
        ) : (
          <span className="badge bg-secondary">Tắt</span>
        )}
      </div>
      <div className="mb-3">
        <button className="btn btn-outline-secondary me-2" onClick={toggleMfa}>
          {mfaEnabled ? "Tắt MFA" : "Bật nhanh (Email OTP)"}
        </button>
        <button className="btn btn-outline-primary" onClick={setupMfa}>
          Thiết lập bằng ứng dụng (TOTP)
        </button>
      </div>

      {mfaQR && (
        <div className="mb-3 p-3 border rounded bg-light">
          <div className="mb-2">
            Quét QR bằng Google Authenticator (hoặc nhập secret):
          </div>
          <img src={mfaQR} alt="MFA QR" style={{ maxWidth: 200 }} />
          <div className="mt-2">
            <code>{mfaSecret}</code>
          </div>
          <div className="mt-3">
            <label className="form-label">Nhập mã 6 số từ ứng dụng</label>
            <input
              className="form-control"
              value={mfaToken}
              onChange={(e) => setMfaToken(e.target.value)}
              placeholder="123456"
            />
            <button className="btn btn-success mt-2" onClick={verifyMfa}>
              Xác nhận
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserProfile;
