import React, {
  useCallback,
  useContext,
  useEffect,
  useMemo,
  useRef,
  useState,
} from "react";
import { useNavigate } from "react-router-dom";
import api from "../services/api";
import ConfirmModal from "./ConfirmModal";
import Toast from "./Toast";
import MfaSetup from "./MfaSetup";
import TransactionDetailsModal from "./TransactionDetailsModal";
import NotificationContext from "../context/NotificationContext";

const maskAccount = (username) => {
  if (!username) return "";
  if (username.length <= 4) return username;
  return "****" + username.slice(-4);
};

const formatCurrency = (amount) => {
  return amount.toLocaleString("vi-VN", { style: "currency", currency: "VND" });
};

const InfoModal = ({ show, title, description, onClose, primaryLabel = "Đã hiểu" }) => {
  if (!show) return null;
  return (
    <div className="modal-backdrop">
      <div className="modal-card">
        <h5>{title}</h5>
        <div className="modal-body">
          <p className="mb-0">{description}</p>
        </div>
        <div className="modal-actions justify-content-end">
          <button className="btn btn-primary" onClick={onClose}>
            {primaryLabel}
          </button>
        </div>
      </div>
    </div>
  );
};

const quickActions = [
  {
    key: "transfer",
    label: "Chuyển khoản",
    icon: "bi-arrow-left-right",
    accent: "action-transfer",
    description: "Chuyển tiền nhanh tới tài khoản nội bộ hoặc ngân hàng khác.",
  },
  {
    key: "deposit",
    label: "Nạp tiền",
    icon: "bi-wallet2",
    accent: "action-deposit",
    description: "Nạp tiền từ thẻ ghi nợ hoặc ví điện tử liên kết.",
  },
  {
    key: "mobile",
    label: "Nạp điện thoại",
    icon: "bi-phone",
    accent: "action-mobile",
    description: "Mua thẻ cào hoặc nạp trực tiếp cho số điện thoại bất kỳ.",
  },
  {
    key: "bill",
    label: "Thanh toán hóa đơn",
    icon: "bi-receipt",
    accent: "action-bill",
    description: "Tự động thanh toán điện, nước, internet và truyền hình.",
  },
  {
    key: "travel",
    label: "Vé máy bay",
    icon: "bi-airplane",
    accent: "action-travel",
    description: "Đặt vé máy bay nội địa và quốc tế, ưu đãi độc quyền.",
  },
  {
    key: "invest",
    label: "Đầu tư",
    icon: "bi-graph-up-arrow",
    accent: "action-invest",
    description: "Mở tài khoản tiết kiệm hoặc danh mục đầu tư sinh lời.",
  },
  {
    key: "support",
    label: "Trợ giúp",
    icon: "bi-life-preserver",
    accent: "action-support",
    description: "Liên hệ tổng đài 24/7 và chatbot tài chính thông minh.",
  },
  {
    key: "promo",
    label: "Ưu đãi",
    icon: "bi-gift",
    accent: "action-promo",
    description: "Khám phá ưu đãi hoàn tiền và voucher mới nhất.",
  },
];

const Dashboard = () => {
  const [balance, setBalance] = useState(0);
  const [transactions, setTransactions] = useState([]);
  const [transferData, setTransferData] = useState({
    toUsername: "",
    amount: "",
    description: "",
  });
  const [depositAmount, setDepositAmount] = useState("");
  const [toast, setToast] = useState({ show: false, type: "success", message: "" });
  const [confirm, setConfirm] = useState({ show: false, payload: null, confirming: false });
  const [dataLoading, setDataLoading] = useState(false);
  const [depositLoading, setDepositLoading] = useState(false);
  const [transferLoading, setTransferLoading] = useState(false);
  const [selectedTx, setSelectedTx] = useState(null);
  const [showTxModal, setShowTxModal] = useState(false);
  const [infoModal, setInfoModal] = useState({ show: false, title: "", description: "" });

  const navigate = useNavigate();
  const token = localStorage.getItem("token");
  const { refresh: refreshNotifications } = useContext(NotificationContext);
  const depositRef = useRef(null);
  const transferRef = useRef(null);

  const getUserFromToken = () => {
    try {
      const raw = localStorage.getItem("token");
      if (!raw) return null;
      const payload = JSON.parse(atob(raw.split(".")[1]));
      return payload;
    } catch (e) {
      return null;
    }
  };
  const user = getUserFromToken();

  useEffect(() => {
    if (!token) {
      navigate("/login");
      return;
    }

    let mounted = true;
    const fetchData = async () => {
      setDataLoading(true);
      try {
        const res = await api.get("/api/transactions/history");
        if (!mounted) return;
        setBalance(res.data.balance);
        setTransactions(res.data.transactions || []);
      } catch (err) {
        if (!mounted) return;
        setToast({
          show: true,
          type: "error",
          message: err.response?.data?.error || "Không thể tải lịch sử giao dịch",
        });
      } finally {
        if (mounted) {
          setDataLoading(false);
        }
      }
    };
    fetchData();
    return () => {
      mounted = false;
    };
  }, [token, navigate]);

  const handleTransferChange = (e) => {
    setTransferData({ ...transferData, [e.target.name]: e.target.value });
  };

  const handleDepositChange = (e) => {
    setDepositAmount(e.target.value);
  };

  const handleTransferSubmit = (e) => {
    e.preventDefault();
    setConfirm({ show: true, payload: { ...transferData }, confirming: false });
  };

  const confirmTransfer = async () => {
    setConfirm((c) => ({ ...c, confirming: true }));
    setTransferLoading(true);
    try {
      const payload = {
        toUsername: confirm.payload?.toUsername,
        amount: confirm.payload?.amount,
        description: confirm.payload?.description,
      };
      const res = await api.post("/api/transactions/transfer", payload);
      setToast({
        show: true,
        type: "success",
        message: res.data.message || "Chuyển khoản thành công",
      });
      setTransferData({ toUsername: "", amount: "", description: "" });
      const historyRes = await api.get("/api/transactions/history");
      setBalance(historyRes.data.balance);
      setTransactions(historyRes.data.transactions || []);
      await refreshNotifications().catch(() => {});
    } catch (err) {
      const msg = err.response?.data?.error || "Chuyển khoản thất bại";
      setToast({ show: true, type: "error", message: msg });
    } finally {
      setTransferLoading(false);
      setConfirm({ show: false, payload: null, confirming: false });
    }
  };

  const handleDepositSubmit = async (e) => {
    e.preventDefault();
    setDepositLoading(true);
    try {
      const res = await api.post("/api/transactions/deposit", {
        amount: Number(depositAmount),
      });
      setToast({
        show: true,
        type: "success",
        message: res.data.message || "Nạp tiền thành công",
      });
      setDepositAmount("");
      const historyRes = await api.get("/api/transactions/history");
      setBalance(historyRes.data.balance);
      setTransactions(historyRes.data.transactions || []);
      await refreshNotifications().catch(() => {});
    } catch (err) {
      setToast({
        show: true,
        type: "error",
        message: err.response?.data?.error || "Nạp tiền thất bại",
      });
    } finally {
      setDepositLoading(false);
    }
  };

  const insights = useMemo(() => {
    if (!transactions.length) {
      return {
        monthlyIncoming: 0,
        monthlyOutgoing: 0,
        pendingCount: 0,
        lastTransaction: null,
      };
    }
    const now = new Date();
    const currentMonth = now.getMonth();
    const currentYear = now.getFullYear();
    const monthly = transactions.filter((tx) => {
      const created = new Date(tx.createdAt);
      return created.getMonth() === currentMonth && created.getFullYear() === currentYear;
    });
    const monthlyIncoming = monthly
      .filter((tx) => tx.type === "deposit")
      .reduce((total, tx) => total + Number(tx.amount || 0), 0);
    const monthlyOutgoing = monthly
      .filter((tx) => tx.type === "transfer")
      .reduce((total, tx) => total + Number(tx.amount || 0), 0);
    const pendingCount = transactions.filter((tx) => tx.status === "pending").length;
    return {
      monthlyIncoming,
      monthlyOutgoing,
      pendingCount,
      lastTransaction: transactions[0] || null,
    };
  }, [transactions]);

  const recentTransactions = useMemo(() => transactions.slice(0, 6), [transactions]);

  const handleQuickAction = useCallback(
    (action) => {
      if (action.key === "deposit") {
        depositRef.current?.scrollIntoView({ behavior: "smooth", block: "center" });
        return;
      }
      if (action.key === "transfer") {
        transferRef.current?.scrollIntoView({ behavior: "smooth", block: "center" });
        return;
      }
      setInfoModal({ show: true, title: action.label, description: action.description });
    },
    []
  );

  const closeInfoModal = useCallback(() => {
    setInfoModal({ show: false, title: "", description: "" });
  }, []);

  const formatTimestamp = (timestamp) => {
    return new Date(timestamp).toLocaleString("vi-VN", {
      hour: "2-digit",
      minute: "2-digit",
      day: "2-digit",
      month: "2-digit",
    });
  };

  const statusLabel = (status) => {
    if (status === "completed") return "Hoàn thành";
    if (status === "pending") return "Đang xử lý";
    return "Thất bại";
  };

  return (
    <div className="dashboard-page">
      <InfoModal
        show={infoModal.show}
        title={infoModal.title}
        description={`${infoModal.description} Tính năng đang được hoàn thiện để mô phỏng đầy đủ trải nghiệm ngân hàng số.`}
        onClose={closeInfoModal}
      />
      <ConfirmModal
        show={confirm.show}
        title={`Xác nhận chuyển ${confirm.payload?.amount || ""} VND`}
        body={
          <div>
            <p>
              Bạn sẽ chuyển <strong>{confirm.payload?.amount}</strong> tới <strong>{confirm.payload?.toUsername}</strong>.
            </p>
            {confirm.payload?.description && (
              <p className="mb-0">Ghi chú: {confirm.payload.description}</p>
            )}
            <p>Bạn có chắc chắn muốn tiếp tục?</p>
          </div>
        }
        onConfirm={confirmTransfer}
        onCancel={() => setConfirm({ show: false, payload: null, confirming: false })}
        confirming={confirm.confirming}
      />
      <Toast
        show={toast.show}
        type={toast.type}
        message={toast.message}
        onClose={() => setToast((current) => ({ ...current, show: false }))}
      />

      <section className="dashboard-hero gradient-card mb-4">
        <div className="dashboard-hero__content">
          <p className="text-uppercase small fw-semibold text-white-50 mb-1">Xin chào</p>
          <h1 className="text-white mb-2">{user?.name || user?.username || user?.email || "Khách hàng"}</h1>
          <p className="text-white-50 mb-3">
            Tài khoản {maskAccount(user?.username || user?.email || "")} · Số dư khả dụng cập nhật theo thời gian thực.
          </p>
          <div className="hero-actions">
            <button
              type="button"
              className="btn btn-light me-2"
              onClick={() => handleQuickAction(quickActions[0])}
            >
              <i className="bi bi-arrow-left-right me-2" aria-hidden></i>
              Chuyển khoản
            </button>
            <button
              type="button"
              className="btn btn-outline-light"
              onClick={() => navigate("/notifications")}
            >
              <i className="bi bi-bell me-2" aria-hidden></i>
              Xem thông báo
            </button>
          </div>
        </div>
        <div className="dashboard-hero__stats">
          <div className="balance-card text-end">
            <span className="balance-card__label text-white-50">Số dư khả dụng</span>
            <div className="balance-card__value">{formatCurrency(balance)}</div>
            {dataLoading && (
              <span className="badge bg-light text-primary mt-2">Đang đồng bộ...</span>
            )}
          </div>
          {insights.lastTransaction ? (
            <div className="hero-meta text-white-50 mt-3">
              <small>Giao dịch gần nhất · {formatTimestamp(insights.lastTransaction.createdAt)}</small>
              <div className="hero-meta__summary text-white">
                {insights.lastTransaction.type === "deposit" ? "Nhận" : "Chuyển"} {formatCurrency(insights.lastTransaction.amount)}
              </div>
            </div>
          ) : (
            <div className="hero-meta text-white-50 mt-3">Chưa có giao dịch nào được ghi nhận.</div>
          )}
        </div>
      </section>

      <section className="card shadow-sm quick-actions-card mb-4">
        <div className="card-header d-flex justify-content-between align-items-center">
          <div>
            <h5 className="mb-0">Tác vụ nhanh</h5>
            <small className="text-muted">Chọn thao tác thường xuyên chỉ với một chạm.</small>
          </div>
        </div>
        <div className="card-body">
          <div className="row row-cols-2 row-cols-md-4 g-3">
            {quickActions.map((action) => (
              <div className="col" key={action.key}>
                <button
                  type="button"
                  className={`quick-action-tile ${action.accent}`}
                  onClick={() => handleQuickAction(action)}
                >
                  <i className={`bi ${action.icon} quick-action-icon`} aria-hidden></i>
                  <span>{action.label}</span>
                </button>
              </div>
            ))}
          </div>
        </div>
      </section>

      <div className="row g-4 mb-4">
        <div className="col-lg-7">
          <div className="card shadow-sm mb-4" ref={depositRef}>
            <div className="card-header d-flex justify-content-between align-items-center">
              <h5 className="mb-0">Nạp tiền vào tài khoản</h5>
              <span className="badge bg-primary-subtle text-primary">Không giới hạn</span>
            </div>
            <div className="card-body">
              <form onSubmit={handleDepositSubmit}>
                <div className="form-floating mb-3">
                  <input
                    id="depositAmount"
                    name="depositAmount"
                    type="number"
                    min="1000"
                    step="1000"
                    value={depositAmount}
                    onChange={handleDepositChange}
                    className="form-control"
                    placeholder="VND"
                    required
                  />
                  <label htmlFor="depositAmount">Số tiền nạp (VND)</label>
                </div>
                <button type="submit" className="btn btn-primary" disabled={depositLoading}>
                  {depositLoading ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden></span>
                      Đang xử lý
                    </>
                  ) : (
                    <>
                      <i className="bi bi-wallet2 me-2" aria-hidden></i>
                      Nạp tiền
                    </>
                  )}
                </button>
              </form>
            </div>
          </div>

          <div className="card shadow-sm" ref={transferRef}>
            <div className="card-header">
              <h5 className="mb-0">Chuyển khoản thông minh</h5>
            </div>
            <div className="card-body">
              <form onSubmit={handleTransferSubmit}>
                <div className="row g-3">
                  <div className="col-12 col-md-6">
                    <div className="form-floating">
                      <input
                        id="toUsername"
                        name="toUsername"
                        value={transferData.toUsername}
                        onChange={handleTransferChange}
                        className="form-control"
                        placeholder="Tên đăng nhập người nhận"
                        required
                      />
                      <label htmlFor="toUsername">Tài khoản nhận</label>
                    </div>
                  </div>
                  <div className="col-12 col-md-6">
                    <div className="form-floating">
                      <input
                        id="transferAmount"
                        name="amount"
                        type="number"
                        min="1000"
                        step="1000"
                        value={transferData.amount}
                        onChange={handleTransferChange}
                        className="form-control"
                        placeholder="VND"
                        required
                      />
                      <label htmlFor="transferAmount">Số tiền (VND)</label>
                    </div>
                  </div>
                  <div className="col-12">
                    <div className="form-floating">
                      <input
                        id="transferDescription"
                        name="description"
                        value={transferData.description}
                        onChange={handleTransferChange}
                        className="form-control"
                        placeholder="Ghi chú chuyển khoản"
                      />
                      <label htmlFor="transferDescription">Ghi chú (không bắt buộc)</label>
                    </div>
                  </div>
                </div>
                <button type="submit" className="btn btn-primary mt-3" disabled={transferLoading}>
                  {transferLoading ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden></span>
                      Đang xử lý
                    </>
                  ) : (
                    <>
                      <i className="bi bi-send me-2" aria-hidden></i>
                      Chuyển khoản
                    </>
                  )}
                </button>
              </form>
            </div>
          </div>
        </div>
        <div className="col-lg-5">
          <div className="card shadow-sm mb-4">
            <div className="card-header">
              <h5 className="mb-0">Tổng quan tháng này</h5>
            </div>
            <div className="card-body">
              <div className="insight-grid">
                <div className="insight-item">
                  <span className="insight-label text-muted">Tiền vào</span>
                  <div className="insight-value text-success">{formatCurrency(insights.monthlyIncoming)}</div>
                </div>
                <div className="insight-item">
                  <span className="insight-label text-muted">Tiền ra</span>
                  <div className="insight-value text-danger">{formatCurrency(insights.monthlyOutgoing)}</div>
                </div>
                <div className="insight-item">
                  <span className="insight-label text-muted">Đang xử lý</span>
                  <div className="insight-value">{insights.pendingCount} giao dịch</div>
                </div>
              </div>
              <div className="mt-3 p-3 bg-light rounded">
                <p className="mb-1 fw-semibold">Tự động hóa bảo mật</p>
                <small className="text-muted">
                  Luồng giao dịch được giám sát thời gian thực với cảnh báo rủi ro bất thường.
                </small>
              </div>
            </div>
          </div>

          <div className="card shadow-sm security-card">
            <div className="card-header border-0">
              <h5 className="mb-0">Bảo mật đa lớp</h5>
            </div>
            <div className="card-body">
              <div className="security-card__content">
                <MfaSetup />
              </div>
            </div>
          </div>
        </div>
      </div>

      <section className="card shadow-sm">
        <div className="card-header d-flex justify-content-between align-items-center">
          <h5 className="mb-0">Giao dịch gần đây</h5>
          <small className="text-muted">Chạm để xem chi tiết và đánh dấu bất thường.</small>
        </div>
        <div className="list-group list-group-flush">
          {recentTransactions.length === 0 ? (
            <div className="list-group-item text-muted text-center py-4">
              Chưa có giao dịch nào.
            </div>
          ) : (
            recentTransactions.map((tx) => (
              <button
                key={tx.id}
                type="button"
                className="list-group-item list-group-item-action d-flex justify-content-between align-items-center"
                onClick={() => {
                  setSelectedTx(tx);
                  setShowTxModal(true);
                }}
              >
                <div>
                  <div className="fw-semibold">
                    {tx.type === "transfer" ? "Chuyển khoản" : tx.type === "deposit" ? "Nạp tiền" : tx.type}
                  </div>
                  <small className="text-muted">
                    {maskAccount(tx.fromUsername)} → {maskAccount(tx.toUsername)} · {formatTimestamp(tx.createdAt)}
                  </small>
                </div>
                <div className="text-end">
                  <div className={tx.type === "deposit" ? "text-success" : "text-danger"}>
                    {tx.type === "deposit" ? "+" : "-"}
                    {formatCurrency(tx.amount)}
                  </div>
                  <small className="badge bg-light text-dark">{statusLabel(tx.status)}</small>
                </div>
              </button>
            ))
          )}
        </div>
      </section>

      <TransactionDetailsModal
        tx={selectedTx}
        show={showTxModal}
        onClose={() => setShowTxModal(false)}
      />
    </div>
  );
};

export default Dashboard;
