import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const maskAccount = (username) => {
  if (!username) return '';
  if (username.length <= 4) return username;
  return '****' + username.slice(-4);
};

const formatCurrency = (amount) => {
  return amount.toLocaleString('vi-VN', { style: 'currency', currency: 'VND' });
};

const Dashboard = () => {
  const [balance, setBalance] = useState(0);
  const [transactions, setTransactions] = useState([]);
  const [transferData, setTransferData] = useState({
    toUsername: '',
    amount: '',
  });
  const [depositAmount, setDepositAmount] = useState('');
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) {
      navigate('/login');
      return;
    }

    let mounted = true;
    const fetchData = async () => {
      try {
        const csrfRes = await axios.get('/api/csrf-token');
        const csrfToken = csrfRes.data.csrfToken;
        const res = await axios.get('/api/transactions/history', { headers: { Authorization: `Bearer ${token}`, 'X-CSRF-Token': csrfToken } });
        if (!mounted) return;
        setBalance(res.data.balance);
        setTransactions(res.data.transactions);
      } catch (err) {
        setError(err.response?.data?.error || 'Không lấy được token bảo mật');
      }
    };
    fetchData();
    return () => { mounted = false; };
  }, [token, navigate]);

  const handleChange = e => {
    setTransferData({ ...transferData, [e.target.name]: e.target.value });
  };

  const handleDepositChange = e => {
    setDepositAmount(e.target.value);
  };

  const handleTransfer = async e => {
    e.preventDefault();
    setMessage(null); setError(null); setLoading(true);
    try {
      const csrfRes = await axios.get('/api/csrf-token');
      const csrfToken = csrfRes.data.csrfToken;
      const res = await axios.post('/api/transactions/transfer', transferData, { headers: { Authorization: `Bearer ${token}`, 'X-CSRF-Token': csrfToken } });
      setMessage(res.data.message || 'Chuyển khoản thành công');
      setTransferData({ toUsername: '', amount: '' });
      const historyRes = await axios.get('/api/transactions/history', { headers: { Authorization: `Bearer ${token}`, 'X-CSRF-Token': csrfToken } });
      setBalance(historyRes.data.balance); setTransactions(historyRes.data.transactions);
    } catch (err) {
      setError(err.response?.data?.error || 'Chuyển khoản thất bại');
    } finally { setLoading(false); }
  };

  const handleDeposit = async e => {
    e.preventDefault(); setMessage(null); setError(null); setLoading(true);
    try {
      const csrfRes = await axios.get('/api/csrf-token');
      const csrfToken = csrfRes.data.csrfToken;
      const res = await axios.post('/api/transactions/deposit', { amount: Number(depositAmount) }, { headers: { Authorization: `Bearer ${token}`, 'X-CSRF-Token': csrfToken } });
      setMessage(res.data.message || 'Nạp tiền thành công'); setDepositAmount('');
      const historyRes = await axios.get('/api/transactions/history', { headers: { Authorization: `Bearer ${token}`, 'X-CSRF-Token': csrfToken } });
      setBalance(historyRes.data.balance); setTransactions(historyRes.data.transactions);
    } catch (err) { setError(err.response?.data?.error || 'Nạp tiền thất bại'); } finally { setLoading(false); }
  };

  return (
    <div className="dashboard-container">
      <h2>Bảng điều khiển</h2>
      <p><strong>Số dư tài khoản:</strong> {formatCurrency(balance)}</p>
      {message && <div className="alert alert-success">{message}</div>}
      {error && <div className="alert alert-error">{error}</div>}
      {loading && <div className="loading">Đang xử lý...</div>}

      <h3>Nạp tiền</h3>
      <form onSubmit={handleDeposit} className="form">
        <div className="form-group">
          <label>Số tiền nạp (VND):</label>
          <input name="depositAmount" type="number" step="1000" value={depositAmount} onChange={handleDepositChange} required placeholder="Nhập số tiền nạp" />
        </div>
        <button type="submit" className="btn btn-primary" disabled={loading}>Nạp tiền</button>
      </form>

      <h3>Chuyển khoản</h3>
      <form onSubmit={handleTransfer} className="form">
        <div className="form-group">
          <label>Đến tài khoản (Tên đăng nhập):</label>
          <input name="toUsername" value={transferData.toUsername} onChange={handleChange} required placeholder="Nhập tên đăng nhập người nhận" />
        </div>
        <div className="form-group">
          <label>Số tiền (VND):</label>
          <input name="amount" type="number" step="1000" value={transferData.amount} onChange={handleChange} required placeholder="Nhập số tiền" />
        </div>
        <button type="submit" className="btn btn-primary" disabled={loading}>Chuyển khoản</button>
      </form>

      <h3>Lịch sử giao dịch</h3>
      <table className="table">
        <thead>
          <tr>
            <th>Từ</th>
            <th>Đến</th>
            <th>Số tiền</th>
            <th>Loại</th>
            <th>Trạng thái</th>
            <th>Mô tả</th>
            <th>Ngày giờ</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map(tx => (
            <tr key={tx.id}>
              <td>{maskAccount(tx.fromUsername)}</td>
              <td>{maskAccount(tx.toUsername)}</td>
              <td>{formatCurrency(tx.amount)}</td>
              <td>{tx.type === 'transfer' ? 'Chuyển khoản' : tx.type === 'deposit' ? 'Nạp tiền' : tx.type}</td>
              <td>{tx.status === 'completed' ? 'Hoàn thành' : tx.status === 'pending' ? 'Đang xử lý' : 'Thất bại'}</td>
              <td>{tx.description}</td>
              <td>{new Date(tx.createdAt).toLocaleString('vi-VN')}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
