import React, { useEffect, useMemo, useState } from "react";
import api from "../services/api";
import LoaderOverlay from "./LoaderOverlay";

const DEFAULT_PAGE_SIZE = 10;

const columns = {
  users: ["username", "email", "role", "status", "balance"],
  transactions: ["id", "type", "amount", "status", "createdAt"],
  logs: ["timestamp", "action", "userId", "ipAddress"],
};

const AdminDashboard = ({ onLogout }) => {
  const [selectedTab, setSelectedTab] = useState("users");
  const [users, setUsers] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(1);
  const [total, setTotal] = useState(0);
  const [filters, setFilters] = useState({ search: "", status: "all" });

  const activeData = useMemo(() => {
    switch (selectedTab) {
      case "transactions":
        return transactions;
      case "logs":
        return logs;
      case "users":
      default:
        return users;
    }
  }, [selectedTab, users, transactions, logs]);

  const activeColumns = columns[selectedTab];

  const fetchData = async () => {
    setLoading(true);
    setError(null);
    try {
      if (selectedTab === "users") {
        const res = await api.get("/api/admin/users", {
          params: {
            page,
            limit: DEFAULT_PAGE_SIZE,
            q: filters.search || undefined,
            status: filters.status !== "all" ? filters.status : undefined,
          },
        });
        setUsers(res.data?.users || []);
        setTotal(res.data?.total || 0);
      } else if (selectedTab === "transactions") {
        const res = await api.get("/api/admin/transactions", {
          params: {
            page,
            limit: DEFAULT_PAGE_SIZE,
            status: filters.status !== "all" ? filters.status : undefined,
          },
        });
        setTransactions(res.data?.transactions || []);
        setTotal(res.data?.total || 0);
      } else {
        const res = await api.get("/api/admin/audit", {
          params: {
            page,
            limit: DEFAULT_PAGE_SIZE,
          },
        });
        setLogs(res.data?.logs || []);
        setTotal(res.data?.total || 0);
      }
    } catch (err) {
      setError(
        err.response?.data?.error ||
          "Không thể tải dữ liệu quản trị. Vui lòng thử lại."
      );
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [selectedTab, page, filters.status]);

  const handleSearch = (event) => {
    const value = event.target.value;
    setFilters((prev) => ({ ...prev, search: value }));
  };

  const handleStatusChange = (event) => {
    setFilters((prev) => ({ ...prev, status: event.target.value }));
    setPage(1);
  };

  const formatCell = (key, value) => {
    if (value === null || value === undefined) return "-";
    if (key === "amount") {
      return Number(value || 0).toLocaleString("vi-VN", {
        style: "currency",
        currency: "VND",
      });
    }
    if (key === "createdAt" || key === "timestamp") {
      return new Date(value).toLocaleString("vi-VN", {
        hour: "2-digit",
        minute: "2-digit",
        day: "2-digit",
        month: "2-digit",
      });
    }
    if (key === "status") {
      const badgeClass =
        value === "completed"
          ? "badge bg-success-subtle text-success"
          : value === "pending"
          ? "badge bg-warning-subtle text-warning"
          : "badge bg-danger-subtle text-danger";
      return <span className={badgeClass}>{value}</span>;
    }
    if (key === "role") {
      return value === "admin" ? "Quản trị viên" : "Người dùng";
    }
    if (key === "balance") {
      return Number(value || 0).toLocaleString("vi-VN", {
        style: "currency",
        currency: "VND",
        minimumFractionDigits: 0,
        maximumFractionDigits: 0,
      });
    }
    if (key === "status" && selectedTab === "users") {
      return value === "locked" ? "Đã khóa" : "Hoạt động";
    }
    return value;
  };

  const handleRefresh = () => {
    fetchData();
  };

  return (
    <div className="admin-dashboard-page">
      <header className="admin-dashboard__header">
        <div>
          <h1 className="admin-dashboard__title">Bảng điều khiển quản trị</h1>
          <p className="admin-dashboard__subtitle">
            Theo dõi hoạt động người dùng, giao dịch và nhật ký bảo mật trong
            thời gian thực.
          </p>
        </div>
        <div className="admin-dashboard__actions">
          <button className="btn btn-light" onClick={handleRefresh}>
            <i className="bi bi-arrow-repeat me-2" aria-hidden></i>
            Làm mới
          </button>
          <button className="btn btn-outline-danger" onClick={onLogout}>
            <i className="bi bi-box-arrow-right me-2" aria-hidden></i>
            Đăng xuất
          </button>
        </div>
      </header>

      <nav className="admin-dashboard__tabs">
        <button
          className={`admin-dashboard__tab ${
            selectedTab === "users" ? "is-active" : ""
          }`}
          onClick={() => {
            setSelectedTab("users");
            setPage(1);
          }}
        >
          Người dùng
        </button>
        <button
          className={`admin-dashboard__tab ${
            selectedTab === "transactions" ? "is-active" : ""
          }`}
          onClick={() => {
            setSelectedTab("transactions");
            setPage(1);
          }}
        >
          Giao dịch
        </button>
        <button
          className={`admin-dashboard__tab ${
            selectedTab === "logs" ? "is-active" : ""
          }`}
          onClick={() => {
            setSelectedTab("logs");
            setPage(1);
          }}
        >
          Nhật ký bảo mật
        </button>
      </nav>

      <section className="admin-dashboard__filters">
        <div className="form-floating">
          <input
            id="adminSearch"
            className="form-control"
            placeholder="Tìm kiếm"
            value={filters.search}
            onChange={handleSearch}
          />
          <label htmlFor="adminSearch">Tìm kiếm</label>
        </div>
        <div className="form-floating">
          <select
            id="adminStatus"
            className="form-select"
            value={filters.status}
            onChange={handleStatusChange}
          >
            <option value="all">Tất cả trạng thái</option>
            <option value="active">Hoạt động</option>
            <option value="locked">Đã khóa</option>
            <option value="pending">Đang xử lý</option>
            <option value="completed">Hoàn thành</option>
            <option value="failed">Thất bại</option>
          </select>
          <label htmlFor="adminStatus">Trạng thái</label>
        </div>
      </section>

      {error && (
        <div className="alert alert-danger" role="alert">
          <i className="bi bi-exclamation-triangle-fill me-2" aria-hidden></i>
          {error}
        </div>
      )}

      <div className="admin-dashboard__table card shadow-sm">
        <div className="table-responsive">
          <table className="table align-middle">
            <thead>
              <tr>
                {activeColumns.map((key) => (
                  <th key={key}>{key}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {activeData.length === 0 ? (
                <tr>
                  <td
                    colSpan={activeColumns.length}
                    className="text-center text-muted py-4"
                  >
                    Không có dữ liệu.
                  </td>
                </tr>
              ) : (
                activeData.map((row) => (
                  <tr key={row.id || row.timestamp}>
                    {activeColumns.map((key) => (
                      <td key={key}>{formatCell(key, row[key])}</td>
                    ))}
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
        <div className="admin-dashboard__pagination">
          <span>
            Trang {page} / {Math.max(1, Math.ceil(total / DEFAULT_PAGE_SIZE))}
          </span>
          <div className="btn-group">
            <button
              className="btn btn-outline-primary"
              onClick={() => setPage((prev) => Math.max(1, prev - 1))}
              disabled={page === 1}
            >
              <i className="bi bi-chevron-left" aria-hidden></i>
            </button>
            <button
              className="btn btn-outline-primary"
              onClick={() =>
                setPage((prev) =>
                  prev * DEFAULT_PAGE_SIZE >= total ? prev : prev + 1
                )
              }
              disabled={page * DEFAULT_PAGE_SIZE >= total}
            >
              <i className="bi bi-chevron-right" aria-hidden></i>
            </button>
          </div>
        </div>
      </div>

      {loading && <LoaderOverlay message="Đang tải dữ liệu quản trị..." />}
    </div>
  );
};

export default AdminDashboard;
